import React, { useState } from "react";

export default function Todo() {
    const [title,setTitle]=useState("");
    const [Description,setDescription]=useState("");
    const [todos,setTodos]=useState([])
    const [error,setError]=useState("");
    const [message,setMessage]=useState("");
    const apiUrl="http://localhost:8000"
    const handleSubmit=()=>{
        setError("")
        if(title.trim() !==''&& Description.trim()!==''){
            fetch(apiUrl + "/todos", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ title, description: Description })
            })
            .then(async (res) => {
                if (res.ok) {
                    setTodos([...todos, { title, description: Description }]);
                    setMessage("Item added successfully ");
                    setTimeout(() => {
                        setMessage("");
                    }, 3000);
                    setError("");
                } else {
                    const errorData = await res.json().catch(() => ({}));
                    setError("Unable to create todo item: " + (errorData.error || res.statusText));
                }
            })
            .catch((err) => {
                setError("Network error: " + err.message);
            });
           

        }
         
    }
    return (
        <>
            <div className="row p-3 bg-black text-light">
                <h1>To Do Works</h1>
            </div>
            <div className="row">
                <h3>Add Item</h3>
                {message && <p className="text-success">{message}</p>}
                <div className="form-group d-flex gap-2">
                    <input
                        placeholder="Title"
                        onChange={(e) => setTitle(e.target.value)}
                        value={title}
                        className="form-control"
                        type="text"
                    />
                    <input
                        placeholder="Description"
                        onChange={(e) => setDescription(e.target.value)}
                        value={Description}
                        className="form-control"
                        type="text"
                    />
                    <button className="btn btn-dark" onClick={handleSubmit}>
                        Submit
                    </button>
                </div>
                {error && <p className="text-danger">{error}</p>}
            </div>
            <div className="row mt-3">
                <h3>Tasks</h3>
                <ul className="list-group">
                    {todos.length === 0 && (
                        <li className="list-group-item">No tasks yet.</li>
                    )}
                    {todos.map((todo, idx) => (
                        <li
                            key={idx}
                            className="list-group-item d-flex justify-content-between align-items-center"
                        >
                            <div className="d-flex flex-column">
                                <span className="fw-bold">{todo.title}</span>
                                <span>{todo.description}</span>
                            </div>
                            <div className="d-flex gap-2">
                                <button className="btn btn-warning" disabled>
                                    Edit
                                </button>
                                <button className="btn btn-danger" disabled>
                                    Delete
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </>
    );
}
