import React, { useEffect, useState } from "react";

export default function Todo() {
    const [title,setTitle]=useState("");
    const [Description,setDescription]=useState("");
    const [todos,setTodos]=useState([])
    const [error,setError]=useState("");
    const [editId,seteditId]=useState(-1);
    const [message,setMessage]=useState("");
     const [edittitle,seteditTitle]=useState("");
    const [editDescription,seteditDescription]=useState("");
    const apiUrl="http://localhost:8000"
    const handleSubmit=()=>{
        setError("")
        if(title.trim() !==''&& Description.trim()!==''){
            fetch(apiUrl + "/todos", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ title, description: Description })
            })
            .then(async (res) => {
                if (res.ok) {
                    setTodos([...todos, { title, description: Description }]);
                    setTitle("");
                    setDescription("");
                    setMessage("Item added successfully ");
                    setTimeout(() => {
                        setMessage("");
                    }, 3000);
                    setError("");
                } else {
                    const errorData = await res.json().catch(() => ({}));
                    setError("Unable to create todo item: " + (errorData.error || res.statusText));
                }
            })
            .catch((err) => {
                setError("Network error: " + err.message);
            });
           

        }
         
    }
    useEffect(()=>{
        getItems()
    },[])
    const getItems=() =>{
        fetch(apiUrl+"/todos")
        .then((res)=> res.json())   
        .then((res)=>{
            setTodos(res)
        })
    } 
    const handleEdit=(item)=>{
        seteditId(item._id);
        seteditTitle(item.title);
        seteditDescription(item.description)
    }
    const handleEditCancel=()=>{
        seteditId(-1);
    }
    const handleUpdate=()=>{
         setError("")
        if(edittitle.trim() !==''&& editDescription.trim()!==''){
            fetch(apiUrl + "/todos/"+editId, {
                method: "PUT",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({title: edittitle, Description: editDescription })
            })
            .then(async (res) => {
                if (res.ok) {
                   const updatedTodos= todos.map((item) =>{
                        if(item._id==editId){
                            item.title= edittitle;
                            item.description=editDescription;
                        }
                        return item;
                    })
                    setTodos(updatedTodos);
                    seteditTitle("");
                    seteditDescription("");
                    setMessage("Item updated successfully ");
                    setTimeout(() => {
                        setMessage("");
                    }, 3000);
                    seteditId(-1)
                    setError("");
                } else {
                    const errorData = await res.json().catch(() => ({}));
                    setError("Unable to create todo item: " + (errorData.error || res.statusText));
                }
            })
            .catch((err) => {
                setError("Network error: " + err.message);
            });
           

        }

    }
    const handledelete=(id)=>{
        if(window.confirm("Are ypu sure to delete this?")){
            fetch(apiUrl+"/todos/"+id,{
                Method: "DELETE"
            })
            .then(()=>{
               const updatedTodos= todos.filter((item)=>item._id!==id)
               setTodos(updatedTodos);
            })
        }

    }
    return (
        <>
            <div
                className="row p-3 text-light align-items-center"
                style={{
                    background: "linear-gradient(90deg, #232526 0%, #414345 100%)",
                    borderRadius: "0 0 16px 16px",
                    boxShadow: "0 4px 16px rgba(0,0,0,0.2)",
                    minHeight: "120px"
                }}
            >
                <img
                    src="https://cdn-icons-png.flaticon.com/512/3176/3176365.png"
                    alt="Todo"
                    style={{ width: 60, height: 60, marginRight: 20 }}
                />
                <h1 style={{ fontWeight: 700, letterSpacing: 2 }}>To Do Works</h1>
            </div>
            <div className="row mt-4">
                <h3 style={{ color: "#232526" }}>Add Item</h3>
                {message && <p className="text-success">{message}</p>}
                <div className="form-group d-flex gap-2 align-items-center">
                    <input
                        placeholder="Title"
                        onChange={(e) => setTitle(e.target.value)}
                        value={title}
                        className="form-control"
                        type="text"
                        style={{ borderColor: "#232526", borderWidth: 2 }}
                    />
                    <input
                        placeholder="Description"
                        onChange={(e) => setDescription(e.target.value)}
                        value={Description}
                        className="form-control"
                        type="text"
                        style={{ borderColor: "#232526", borderWidth: 2 }}
                    />
                    <button
                        className="btn"
                        style={{
                            background: "linear-gradient(90deg, #232526 0%, #414345 100%)",
                            color: "#fff",
                            fontWeight: 600
                        }}
                        onClick={handleSubmit}
                    >
                        Submit
                    </button>
                </div>
                {error && <p className="text-danger">{error}</p>}
            </div>
            <div className="row mt-4">
                <h3 style={{ color: "#232526" }}>Tasks</h3>
                <ul className="list-group">
                    {todos.map((item) => (
                        <li
                            key={item._id || item.title}
                            className="list-group-item d-flex justify-content-between align-items-center my-2"
                            style={{
                                background: "linear-gradient(90deg, #f8ffae 0%, #43c6ac 100%)",
                                borderRadius: 12,
                                boxShadow: "0 2px 8px rgba(67,198,172,0.1)"
                            }}
                        >
                            <div className="d-flex flex-column me-2" style={{ flex: 1 }}>
                                {editId === -1 || editId !== item._id ? (
                                    <>
                                        <span className="fw-bold" style={{ color: "#232526", fontSize: 18 }}>
                                            {item.title}
                                        </span>
                                        <span style={{ color: "#414345" }}>{item.description}</span>
                                    </>
                                ) : (
                                    <div className="d-flex gap-2 me-1">
                                        <input
                                            placeholder="Title"
                                            onChange={(e) => seteditTitle(e.target.value)}
                                            value={edittitle}
                                            className="form-control"
                                            type="text"
                                            style={{ borderColor: "#232526", borderWidth: 2 }}
                                        />
                                        <input
                                            placeholder="Description"
                                            onChange={(e) => seteditDescription(e.target.value)}
                                            value={editDescription}
                                            className="form-control"
                                            type="text"
                                            style={{ borderColor: "#232526", borderWidth: 2 }}
                                        />
                                    </div>
                                )}
                            </div>
                            <div className="d-flex gap-2">
                                {editId === -1 || editId !== item._id ? (
                                    <>
                                        <button
                                            className="btn"
                                            style={{
                                                background: "linear-gradient(90deg, #43c6ac 0%, #191654 100%)",
                                                color: "#fff",
                                                fontWeight: 600
                                            }}
                                            onClick={() => handleEdit(item)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="btn"
                                            style={{
                                                background: "linear-gradient(90deg, #ff5858 0%, #f09819 100%)",
                                                color: "#fff",
                                                fontWeight: 600
                                            }}
                                            onClick={() => handledelete(item._id)}
                                        >
                                            Delete
                                        </button>
                                    </>
                                ) : (
                                    <>
                                        <button
                                            className="btn"
                                            style={{
                                                background: "linear-gradient(90deg, #11998e 0%, #38ef7d 100%)",
                                                color: "#fff",
                                                fontWeight: 600
                                            }}
                                            onClick={handleUpdate}
                                        >
                                            Update
                                        </button>
                                        <button
                                            className="btn"
                                            style={{
                                                background: "linear-gradient(90deg, #f7971e 0%, #ffd200 100%)",
                                                color: "#232526",
                                                fontWeight: 600
                                            }}
                                            onClick={handleEditCancel}
                                        >
                                            Cancel
                                        </button>
                                    </>
                                )}
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </>
    );
}
