import React, { useEffect, useState } from "react";

export default function Todo() {
    const [title,setTitle]=useState("");
    const [Description,setDescription]=useState("");
    const [todos,setTodos]=useState([])
    const [error,setError]=useState("");
    const [editId,seteditId]=useState(-1);
    const [message,setMessage]=useState("");
     const [edittitle,seteditTitle]=useState("");
    const [editDescription,seteditDescription]=useState("");
    const apiUrl="http://localhost:8000"
    const handleSubmit=()=>{
        setError("")
        if(title.trim() !==''&& Description.trim()!==''){
            fetch(apiUrl + "/todos", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ title, description: Description })
            })
            .then(async (res) => {
                if (res.ok) {
                    setTodos([...todos, { title, description: Description }]);
                    setTitle("");
                    setDescription("");
                    setMessage("Item added successfully ");
                    setTimeout(() => {
                        setMessage("");
                    }, 3000);
                    setError("");
                } else {
                    const errorData = await res.json().catch(() => ({}));
                    setError("Unable to create todo item: " + (errorData.error || res.statusText));
                }
            })
            .catch((err) => {
                setError("Network error: " + err.message);
            });
           

        }
         
    }
    useEffect(()=>{
        getItems()
    },[])
    const getItems=() =>{
        fetch(apiUrl+"/todos")
        .then((res)=> res.json())   
        .then((res)=>{
            setTodos(res)
        })
    } 
    const handleEdit=(item)=>{
        seteditId(item._id);
        seteditTitle(item.title);
        seteditDescription(item.description)
    }
    const handleEditCancel=()=>{
        seteditId(-1);
    }
    const handleUpdate=()=>{
         setError("")
        if(edittitle.trim() !==''&& editDescription.trim()!==''){
            fetch(apiUrl + "/todos/"+editId, {
                method: "PUT",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({title: edittitle, Description: editDescription })
            })
            .then(async (res) => {
                if (res.ok) {
                   const updatedTodos= todos.map((item) =>{
                        if(item._id==editId){
                            item.title= edittitle;
                            item.description=editDescription;
                        }
                        return item;
                    })
                    setTodos(updatedTodos);
                    setMessage("Item updated successfully ");
                    setTimeout(() => {
                        setMessage("");
                    }, 3000);
                    seteditId(-1)
                    setError("");
                } else {
                    const errorData = await res.json().catch(() => ({}));
                    setError("Unable to create todo item: " + (errorData.error || res.statusText));
                }
            })
            .catch((err) => {
                setError("Network error: " + err.message);
            });
           

        }

    }
    const handledelete=(id)=>{
        if(window.confirm("Are ypu sure to delete this?")){
            fetch(apiUrl+"/todos/"+id,{
                Method: "DELETE"
            })
            .then(()=>{
               const updatedTodos= todos.filter((item)=>item._id!==id)
               setTodos(updatedTodos);
            })
        }

    }
    return <>
    <div className="row p-3 bg-black text-light ">
        <h1>To Do works </h1>

    </div>
    <div className="row">
        <h3> Add item </h3>
       {message &&<p className="text-success">{message}</p>}
        <div className="form-group d-flex gap-2">
            <input placeholder="Title" onChange={(e)=>setTitle(e.target.value)} value={title} className="form-control" type="text"/>
            <input placeholder="Description"onChange={(e)=>setDescription(e.target.value)} value={Description} className="form-control" type="text" />
            <button className="btn btn-dark" onClick={handleSubmit}>Submit</button>
        </div>
       {error &&<p className="text-danger">{error}</p>}
    </div>
    <div className="row mt-3"> 
        <h3 className="">Tasks</h3>
        <ul className="list-group">
            {
                todos.map((item)=> 
                <li className="list-group-item bg-light d-flex justify-content-between align-items-center my-2">
                <div className="d-flex flex-column me-2">{
                    editId==-1||editId!== item._id?<>
                        <span className="fw-bold ">{item.title}</span>
                <span>{item.description}</span>
                    </>:<>
                      <div className="d-flex gap-2 me-1">
                         <input placeholder="Title" onChange={(e)=>seteditId(e.target.value)} value={edittitle} className="form-control" type="text"/>
            <input placeholder="Description"onChange={(e)=>seteditDescription(e.target.value)} value={editDescription} className="form-control" type="text" />

                      </div>
                    </>

                    }
              
                </div>
                <div className="d-flex gap-2">
            
            { editId==-1||editId!==item._id ? <button className="btn btn-dark" onClick={() =>handleEdit(item)}>Edit</button>:<button className="btn btn-success" onClick={handleUpdate}>Update</button>}
               { editId==-1||editId!==item._id ? <button className="btn btn-danger" onClick={()=>handledelete(item._id)}>Delete</button>:
                <button className="btn btn-warning" onClick={handleEditCancel}>Cancel</button>}
                </div>
            </li>
            
                    
                )
            }
           
             </ul>

    </div>
    </>
}
