import { useState } from "react";

export default function Todo() {
    const [title,setTitle]=useState("");
    const [Description,setDescription]=useState("");
    const [todos,setTodos]=useState([])
    const apiUrl="http://localhost:3000/"
    const handleSubmit=()=>{
        if(title.trim() !==""&& Description.trim()!==""){
            fetch(apiUrl) 
            setTodos([...todos,{title,Description}])

        }
         
    }
    return <>
    <div className="row p-3 bg-black text-light ">
        <h1>To Do works </h1>

    </div>
    <div className="row">
        <h3> Add item </h3>
        <p className="text-success">Item added successfully</p>
        <div className="form-group d-flex gap-2">
            <input placeholder="Title" onChange={(e)=>setTitle(e.target.value)} value={title} className="form-control" type="text"/>
            <input placeholder="Description" className="form-control" type="text" />
            <button className="btn btn-dark" onClick={handleSubmit}>Submit</button>
        </div>

    </div>
    </>
}
